import React, { useState, useEffect } from 'react';
import './Marketing.scss';

const Marketing = () => {
  const [index, setIndex] = useState(0);

  const nextScreen = () => {
    if (index < indexMax()) {
      setIndex(index + 1);
    }
  };

  const prevScreen = () => {
    if (index > 0) {
      setIndex(index - 1);
    }
  };

  const updateScreen = () => {
    reset();
    goTo(index);
    setBtns();
  };

  const setBtns = () => {
    const $nextBtn = document.querySelector('.next-screen');
    const $prevBtn = document.querySelector('.prev-screen');
    const $lastBtn = document.querySelector('.finish');

    if (index === indexMax()) {
      $nextBtn.disabled = true;
      $prevBtn.disabled = false;
      $lastBtn.classList.add('active');
      $lastBtn.disabled = false;
    } else if (index === 0) {
      $nextBtn.disabled = false;
      $prevBtn.disabled = true;
      $lastBtn.classList.remove('active');
      $lastBtn.disabled = true;
    } else {
      $nextBtn.disabled = false;
      $prevBtn.disabled = false;
      $lastBtn.classList.remove('active');
      $lastBtn.disabled = true;
    }
  };

  const goTo = (index) => {
    const screens = document.querySelectorAll('.screen');
    const dots = document.querySelectorAll('.dot');

    screens.forEach((screen, i) => {
      if (i === index) {
        screen.classList.add('active');
        dots[i].classList.add('active');
      } else {
        screen.classList.remove('active');
        dots[i].classList.remove('active');
      }
    });
  };

  const reset = () => {
    const screens = document.querySelectorAll('.screen');
    const dots = document.querySelectorAll('.dot');

    screens.forEach((screen) => screen.classList.remove('active'));
    dots.forEach((dot) => dot.classList.remove('active'));
  };

  const indexMax = () => {
    const screens = document.querySelectorAll('.screen');
    return screens.length - 1;
  };

  const closeModal = () => {
    const walkthrough = document.querySelector('.walkthrough');
    const shade = document.querySelector('.shade');
    walkthrough.classList.remove('reveal');
    setTimeout(() => {
      walkthrough.classList.remove('show');
      setIndex(0);
      updateScreen();
    }, 200);
  };

  const openModal = () => {
    const walkthrough = document.querySelector('.walkthrough');
    const shade = document.querySelector('.shade');
    walkthrough.classList.add('show');
    setTimeout(() => {
      walkthrough.classList.add('reveal');
    }, 200);
    updateScreen();
  };

  useEffect(() => {
    openModal();

    const keyDownHandler = (e) => {
      switch (e.which) {
        case 37: // left arrow key
          prevScreen();
          break;
        case 38: // up arrow key
          openModal();
          break;
        case 39: // right arrow key
          nextScreen();
          break;
        case 40: // down arrow key
          closeModal();
          break;
        default:
          return;
      }
      e.preventDefault();
    };

    document.addEventListener('keydown', keyDownHandler);
    return () => document.removeEventListener('keydown', keyDownHandler);
  }, []);

  return (
    <div className="walkthrough show reveal">
      <div className="walkthrough-pagination">
        <a className="dot active"></a>
        <a className="dot"></a>
        <a className="dot"></a>
        <a className="dot"></a>
        <a className="dot"></a>
      </div>
      <div className="walkthrough-body">
        <ul className="screens animate">
          <li className="screen active">
            <div className="media logo">
              <img className="logo" src="https://s3.amazonaws.com/jebbles-codepen/icon.png" alt="Logo" />
            </div>
            <h3>Product Intro<br />Walkthrough</h3>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris.</p>
          </li>
          {/* Add other screens here */}
        </ul>
      </div>
      <div className="walkthrough-footer">
        <button className="button next-screen" onClick={nextScreen}>Next</button>
        <button className="button finish close" disabled>Finish</button>
      </div>
    </div>
  );
};

export default Marketing;
