import React, { useEffect } from 'react';
import styles from './Marketing.scss'; // Assuming you are using CSS Modules for local scoping


const images = {
    logo: 'https://s3.amazonaws.com/jebbles-codepen/icon.png',
    icon1: 'https://s3.amazonaws.com/jebbles-codepen/book_icon_1.png',
    icon2: 'https://s3.amazonaws.com/jebbles-codepen/book_icon_2.png',
    icon3: 'https://s3.amazonaws.com/jebbles-codepen/book_icon_3.png',
    barIconAxis: 'https://s3.amazonaws.com/jebbles-codepen/bar_icon_axis.png',
    barIcon3: 'https://s3.amazonaws.com/jebbles-codepen/bar_icon_3.png',
    barIcon2: 'https://s3.amazonaws.com/jebbles-codepen/bar_icon_2.png',
    barIcon1: 'https://s3.amazonaws.com/jebbles-codepen/bar_icon_1.png',
    fileIcon1: 'https://s3.amazonaws.com/jebbles-codepen/file_icon_1.png',
    fileIcon2: 'https://s3.amazonaws.com/jebbles-codepen/file_icon_2.png',
    fileIcon3: 'https://s3.amazonaws.com/jebbles-codepen/file_icon_3.png',
    fileIcon4: 'https://s3.amazonaws.com/jebbles-codepen/file_icon_4.png',
    commIcon1: 'https://s3.amazonaws.com/jebbles-codepen/comm_icon_1.png',
    commIcon2: 'https://s3.amazonaws.com/jebbles-codepen/comm_icon_2.png',
  };


const Marketing = () => {
  useEffect(() => {
    let index = 0;

    const nextScreen = () => {
      if (index < indexMax()) {
        index++;
        updateScreen();
      }
    };

    const prevScreen = () => {
      if (index > 0) {
        index--;
        updateScreen();
      }
    };

    const updateScreen = () => {
      reset();
      goTo(index);
      setBtns();
    };

    const setBtns = () => {
      const $nextBtn = document.querySelector('.next-screen');
      const $prevBtn = document.querySelector('.prev-screen');
      const $lastBtn = document.querySelector('.finish');

      if (index === indexMax()) {
        $nextBtn.disabled = true;
        $prevBtn.disabled = false;
        $lastBtn.classList.add('active');
        $lastBtn.disabled = false;
      } else if (index === 0) {
        $nextBtn.disabled = false;
        $prevBtn.disabled = true;
        $lastBtn.classList.remove('active');
        $lastBtn.disabled = true;
      } else {
        $nextBtn.disabled = false;
        $prevBtn.disabled = false;
        $lastBtn.classList.remove('active');
        $lastBtn.disabled = true;
      }
    };

    const goTo = (index) => {
      const screens = document.querySelectorAll('.screen');
      const dots = document.querySelectorAll('.dot');

      screens.forEach((screen, i) => {
        if (i === index) {
          screen.classList.add('active');
          dots[i].classList.add('active');
        } else {
          screen.classList.remove('active');
          dots[i].classList.remove('active');
        }
      });
    };

    const reset = () => {
      const screens = document.querySelectorAll('.screen');
      const dots = document.querySelectorAll('.dot');

      screens.forEach((screen) => {
        screen.classList.remove('active');
      });

      dots.forEach((dot) => {
        dot.classList.remove('active');
      });
    };

    const indexMax = () => {
      const screens = document.querySelectorAll('.screen');
      return screens.length - 1;
    };

    document.querySelector('.next-screen').addEventListener('click', nextScreen);
    document.querySelector('.prev-screen').addEventListener('click', prevScreen);
    document.querySelector('.close').addEventListener('click', closeModal);
    document.querySelector('.open-walkthrough').addEventListener('click', openModal);

    openModal(); // Optional: Open the modal when the component mounts

    // Optionally, use arrow keys to navigate walkthrough
    document.addEventListener('keydown', (e) => {
      switch (e.which) {
        case 37:
          // left
          prevScreen();
          break;
        case 38:
          // up
          openModal();
          break;
        case 39:
          // right
          nextScreen();
          break;
        case 40:
          // down
          closeModal();
          break;
        default:
          break;
      }
      e.preventDefault();
    });
  }, []);

  return (
    <div class="marketing-walkthrough">
    <div className={styles.walkthrough}>
          <div className={styles.walkthrough}>
      <button className={styles.openWalkthrough}>Start</button>
      <div className={`${styles.walkthroughBody} ${styles.show} ${styles.reveal}`}>
        <div className={styles.walkthroughPagination}>
          <a className={`${styles.dot} ${styles.active}`} />
          <a className={styles.dot} />
          <a className={styles.dot} />
          <a className={styles.dot} />
          <a className={styles.dot} />
        </div>
        <ul className={styles.screens}>
          {/* Screen 1 */}
          <li className={`${styles.screen} ${styles.active}`}>
            <div className={`${styles.media} ${styles.logo}`}>
              <img className={styles.logo} src={images.logo} alt="Logo" />
            </div>
            <h3>
              Product Intro
              <br />
              Walkthrough
            </h3>
            <p>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor
              incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud
              exercitation ullamco laboris.
            </p>
          </li>

          {/* Screen 2 */}
          <li className={styles.screen}>
            <div className={styles.media}>
              <img className={styles.icon} src={images.icon1} alt="Icon 1" />
              <img className={styles.icon} src={images.icon2} alt="Icon 2" />
              <img className={styles.icon} src={images.icon3} alt="Icon 3" />
            </div>
            <h3>
              Data and File
              <br />
              Management
            </h3>
            <p>
