import React, { Component } from 'react';
import './CreatorCarousel.css';
import '../../Assets/Laptopandsmartphonemockups.PNG'

class CreatorCarousel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentSlide: 0,
    };
  }

  nextSlide = () => {
    this.setState((prevState) => ({
      currentSlide: (prevState.currentSlide + 1) % this.slides.length,
    }));
  };

  prevSlide = () => {
    this.setState((prevState) => ({
      currentSlide:
        (prevState.currentSlide - 1 + this.slides.length) % this.slides.length,
    }));
  };

  slides = [
    {
      image: '../../Assets/Laptopandsmartphonemockups.PNG',
      title: 'Slide 1',
      description: 'This is the first slide.',
    },
    {
      image: 'path/to/your/image2.jpg',
      title: 'Slide 2',
      description: 'This is the second slide.',
    },
    {
      image: 'path/to/your/image3.jpg',
      title: 'Slide 3',
      description: 'This is the third slide.',
    },
    // Add more slides as needed
  ];

  render() {
    const { currentSlide } = this.state;

    return (
      <div className="creator-carousel-container">
        <div className="creator-carousel">
          <img src={this.slides[currentSlide].image} alt={`Slide ${currentSlide + 1}`} />
          <div className="creator-carousel-content">
            <h3>{this.slides[currentSlide].title}</h3>
            <p>{this.slides[currentSlide].description}</p>
          </div>
          <button onClick={this.prevSlide}>Previous</button>
          <button onClick={this.nextSlide}>Next</button>
        </div>
      </div>
    );
  }
}

export default CreatorCarousel;
