/**@jsxRuntime classic*/
/**@jsx jsx */
import { css, jsx } from "@emotion/react";

const Buttons = ({ setAll, setBreakfast, setLunch, setShakes }) => {
  const breakpoints = [576, 768, 992, 1200];

  const mq = breakpoints.map((bp) => `@media (max-width: ${bp}px)`);

  return (
    <div
      className="Buttons"
    >
      <button
        onClick={() => {
          setAll(true);
          setBreakfast(false);
          setLunch(false);
          setShakes(false);
        }}
      >
        <span>All</span>
      </button>

      <button
        onClick={() => {
          setBreakfast(true);
          setAll(false);
          setLunch(false);
          setShakes(false);
        }}
      >
        <span>Starter</span>
      </button>

      <button
        onClick={() => {
          setLunch(true);
          setAll(false);
          setBreakfast(false);
          setShakes(false);
        }}
      >
        <span>Advanced</span>
      </button>

      <button
        onClick={() => {
          setShakes(true);
          setAll(false);
          setBreakfast(false);
          setLunch(false);
        }}
      >
        <span>Special</span>
      </button>
    </div>
  );
};

export default Buttons;
