import { Component } from 'react';
import { Container, Col, Row, Navbar } from "react-bootstrap";
import "./CreatorCarousel.css"

const slides = [
    {
      id: 1,
      src: 'image1.jpg',
      alt: 'Image 1',
      text: 'Lorem ipsadafdsfaum dolor sit amet, consectetur adipiscing elit.',
    },
    {
      id: 2,
      src: 'image2.jpg',
      alt: 'Image 2',
      text: 'Praesent euismod sapien risus, quis vehicula quam venenatis eu.',
    },
    {
      id: 3,
      src: 'image3.jpg',
      alt: 'Image 3',
      text: 'Integer pulvinar elit non lorem pharetra, ut fermentum turpis finibus.',
    },
  ];


  class CreatorCarousel extends Component {
    constructor(props) {
      super(props);
      this.state = {
        activeSlideIndex: 0,
      };
    }

    nextSlide = () => {
      const { activeSlideIndex } = this.state;
      const { slides } = this.props;
      const nextIndex = (activeSlideIndex + 1) % slides.length;
      this.setState({ activeSlideIndex: nextIndex });
    };

    prevSlide = () => {
      const { activeSlideIndex } = this.state;
      const { slides } = this.props;
      const prevIndex = (activeSlideIndex - 1 + slides.length) % slides.length;
      this.setState({ activeSlideIndex: prevIndex });
    };

    render() {
      const { slides } = this.props;
      const { activeSlideIndex } = this.state;

      return (
        <div className="creator-carousel-container">
          <div
            className="creator-carousel"
            style={{ transform: `translateX(-${activeSlideIndex * 100}%)` }}
          >
            {slides.map((slide, index) => (
              <div
                key={index}
                className={`creator-slide ${index === activeSlideIndex ? 'active' : ''}`}
                style={{ backgroundImage: `url(${slide.imageUrl})` }}
              >
                <div className="creator-content">
                  <h3 className="creator-h3">{slide.title}</h3>
                  <p className="creator-p">{slide.description}</p>
                </div>
              </div>
            ))}
          </div>
          <button className="creator-prev-button" onClick={this.prevSlide}>
            &lt;
          </button>
          <button className="creator-next-button" onClick={this.nextSlide}>
            &gt;
          </button>
        </div>
      );
    }
  }

  export default CreatorCarousel;
