import React, { Component } from 'react';
import './CreatorCarousel.css';
import slideOneImage from '../../Assets/laptopandsmartphonemockups.png'
import slideTwoImage from '../../Assets/branddesignslide2.png'

class CreatorCarousel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentSlide: 0,
    };
  }

  goToPrevSlide = () => {
    this.setState((prevState) => ({
      currentSlide: (prevState.currentSlide - 1 + this.props.slides.length) % this.props.slides.length,
    }));
  };

  goToNextSlide = () => {
    this.setState((prevState) => ({
      currentSlide: (prevState.currentSlide + 1) % this.props.slides.length,
    }));
  };
  slides = [
    {
      image: slideOneImage,
      title: 'Website Creation',
      description: 'Design and develop breathtaking websites and applications',
    },
    {
      image: slideTwoImage,
      title: 'Brand Design',
      description: 'Create a brand design that people will want to represent',
    },
    {
      image: '../../Assets/laptopandsmartphonemockups.png',
      title: 'Slide 3',
      description: 'This is the third slide.',
    },
    // Add more slides as needed
  ];

  render() {
    const { currentSlide } = this.state;
    const currentImage = this.slides[currentSlide].image;
    const currentTitle = this.slides[currentSlide].title;
    const currentDescription = this.slides[currentSlide].description;
    console.log(currentImage)

    return (
        <div className="creator-carousel-container">
          <div className="creator-carousel">
            {this.slides.map((slide, index) => (
              <div
                key={index}
                className={`carousel-slide ${index === currentSlide ? "" : "hide"}`}
              >
                <img src={slide.image} alt={`Slide ${index}`} />
                <div className="creator-carousel-content">
                  <h3>{slide.title}</h3>
                  <p>{slide.description}</p>
                </div>
              </div>
            ))}
            <button
              className="creator-carousel-button prev-button"
              onClick={this.goToPrevSlide}
            >
              Prev
            </button>
            <button
              className="creator-carousel-button next-button"
              onClick={this.goToNextSlide}
            >
              Next
            </button>
          </div>
        </div>
    );
  }
}

export default CreatorCarousel;
