import React, { Component } from 'react';
import './CreatorCarousel.css';

class CreatorCarousel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      activeSlideIndex: 0,
    };
  }

  nextSlide = () => {
    const { activeSlideIndex } = this.state;
    const { slides } = this.props;
    const nextIndex = (activeSlideIndex + 1) % slides.length;
    this.setState({ activeSlideIndex: nextIndex });
  };

  prevSlide = () => {
    const { activeSlideIndex } = this.state;
    const { slides } = this.props;
    const prevIndex = (activeSlideIndex - 1 + slides.length) % slides.length;
    this.setState({ activeSlideIndex: prevIndex });
  };

  render() {
    const { slides } = this.props;
    const { activeSlideIndex } = this.state;

    return (
      <div className="neumorphism-carousel-container">
        <div
          className="neumorphism-carousel"
          style={{ transform: `translateX(-${activeSlideIndex * 100}%)` }}
        >
          {slides.map((slide, index) => (
            <div
              key={index}
              className={`neumorphism-slide ${index === activeSlideIndex ? 'active' : ''}`}
              style={{ backgroundImage: `url(${slide.imageUrl})` }}
            >
              <div className="neumorphism-content">
                <h3 className="neumorphism-h3">{slide.title}</h3>
                <p className="neumorphism-p">{slide.description}</p>
              </div>
            </div>
          ))}
        </div>
        <button className="neumorphism-prev-button" onClick={this.prevSlide}>
          &lt;
        </button>
        <button className="neumorphism-next-button" onClick={this.nextSlide}>
          &gt;
        </button>
      </div>
    );
  }
}

export default CreatorCarousel;
