import { Component } from 'react';
import { Container, Col, Row, Navbar } from "react-bootstrap";
import "./CreatorCarousel.module.css"

const images = [
    {
      id: 1,
      src: 'image1.jpg',
      alt: 'Image 1',
      text: 'Lorem ipsadafdsfaum dolor sit amet, consectetur adipiscing elit.',
    },
    {
      id: 2,
      src: 'image2.jpg',
      alt: 'Image 2',
      text: 'Praesent euismod sapien risus, quis vehicula quam venenatis eu.',
    },
    {
      id: 3,
      src: 'image3.jpg',
      alt: 'Image 3',
      text: 'Integer pulvinar elit non lorem pharetra, ut fermentum turpis finibus.',
    },
  ];


class CreatorCarousel extends Component {
    constructor(props) {
        super(props);
        this.state = {
          currentIndex: 0,
        };
      }

      handlePrev = () => {
        this.setState((prevState) => ({
          currentIndex: prevState.currentIndex === 0 ? images.length - 1 : prevState.currentIndex - 1,
        }));
      };

      handleNext = () => {
        this.setState((prevState) => ({
          currentIndex: prevState.currentIndex === images.length - 1 ? 0 : prevState.currentIndex + 1,
        }));
      };

      render() {
        const { currentIndex } = this.state;

        return (
          <div className="carousel-container">
            <div className="carousel">
              {images.map((image, index) => (
                <div
                  key={image.id}
                  className={`slide ${index === currentIndex ? 'active' : ''}`}
                //   style={{ backgroundImage: `url(${image.src})` }}
                >
                  <div className="content">
                    <h3>{image.alt}</h3>
                    <p>{image.text}</p>
                  </div>
                </div>
              ))}
            </div>
            <button className="prev-button" onClick={this.handlePrev}>
              &lt;
            </button>
            <button className="next-button" onClick={this.handleNext}>
              &gt;
            </button>
          </div>
        );
      }
    }
    export default CreatorCarousel
