import React from 'react';
import Iframe from 'react-iframe';

const GoHighlevelContactForm = () => {
    const iframeRef = useRef(null);
    const [iframeHeight, setIframeHeight] = useState(0);

    const handleIframeLoad = () => {
      const iframeDocument = iframeRef.current?.contentDocument;
      const iframeBody = iframeDocument?.body;
      if (iframeBody) {
        const newHeight = iframeBody.scrollHeight;
        setIframeHeight(newHeight);
      }
    };
  return (
    <div style={{ width: '100%', height: iframeHeight }}>
      <Iframe
        src="https://link.youshouldgetpluggedin.io/widget/form/6pVRfWk3MNldxb1f6a9E"
        style={{ width: '100%', height: '100%', border: 'none', borderRadius: '4px' }}
        id="inline-6pVRfWk3MNldxb1f6a9E"
        data-layout="{'id':'INLINE'}"
        data-trigger-type="alwaysShow"
        data-trigger-value=""
        data-activation-type="alwaysActivated"
        data-activation-value=""
        data-deactivation-type="neverDeactivate"
        data-deactivation-value=""
        data-form-name="Form 3"
        data-height="619"
        data-layout-iframe-id="inline-6pVRfWk3MNldxb1f6a9E"
        data-form-id="6pVRfWk3MNldxb1f6a9E"
        title="Form 3"
      />
      <script src="https://link.youshouldgetpluggedin.io/js/form_embed.js"></script>
    </div>
  );
};

export default GoHighlevelContactForm;
