import React, { Component } from 'react';
import './HeroBanner.css';

class HeroBanner extends Component {
  scrollToContactForm = () => {
    const contactForm = document.getElementById('contactForm');
    if (contactForm) {
      const offsetTop = contactForm.getBoundingClientRect().top;
      const headerHeight = 58; // Replace with your actual header height
      const smoothScrollOptions = {
        behavior: 'smooth',
        block: 'start',
        inline: 'nearest',
      };
      window.scrollTo({ top: offsetTop - headerHeight, ...smoothScrollOptions });
    }
  };

  render() {
    return (
      <div className="neumorphic-hero-banner">
        <div className="content">
          <h1 className="title">Welcome to Our Website</h1>
          <p className="subtitle">Explore the possibilities!</p>
          <button className="scroll-button" onClick={this.scrollToContactForm}>
            Contact Us
          </button>
        </div>
      </div>
    );
  }
}

export default HeroBanner;
