import React, { useState } from 'react';
import './Marketing.scss';

const Marketing = () => {
  const [index, setIndex] = useState(0);

  const nextScreen = () => {
    if (index < indexMax()) {
      setIndex(index + 1);
    }
  };

  const prevScreen = () => {
    if (index > 0) {
      setIndex(index - 1);
    }
  };

  const updateScreen = () => {
    reset();
    goTo(index);
    setBtns();
  };

  const setBtns = () => {
    const $nextBtn = document.querySelector('.next-screen');
    const $prevBtn = document.querySelector('.prev-screen');
    const $lastBtn = document.querySelector('.finish');

    if (index === indexMax()) {
      $nextBtn.disabled = true;
      $prevBtn.disabled = false;
      $lastBtn.classList.add('active');
      $lastBtn.disabled = false;
    } else if (index === 0) {
      $nextBtn.disabled = false;
      $prevBtn.disabled = true;
      $lastBtn.classList.remove('active');
      $lastBtn.disabled = true;
    } else {
      $nextBtn.disabled = false;
      $prevBtn.disabled = false;
      $lastBtn.classList.remove('active');
      $lastBtn.disabled = true;
    }
  };

  const goTo = (index) => {
    const screens = document.querySelectorAll('.screen');
    const dots = document.querySelectorAll('.dot');

    screens.forEach((screen, idx) => {
      if (idx === index) {
        screen.classList.add('active');
        dots[idx].classList.add('active');
      }
    });
  };

  const reset = () => {
    const screens = document.querySelectorAll('.screen');
    const dots = document.querySelectorAll('.dot');

    screens.forEach((screen) => {
      screen.classList.remove('active');
    });

    dots.forEach((dot) => {
      dot.classList.remove('active');
    });
  };

  const indexMax = () => {
    const screens = document.querySelectorAll('.screen');
    return screens.length - 1;
  };

  const closeModal = () => {
    const walkthrough = document.querySelector('.walkthrough');
    const shade = document.querySelector('.shade');
    walkthrough.classList.remove('reveal');
    setTimeout(() => {
      walkthrough.classList.remove('show');
      setIndex(0);
      updateScreen();
    }, 200);
  };

  const openModal = () => {
    const walkthrough = document.querySelector('.walkthrough');
    const shade = document.querySelector('.shade');
    walkthrough.classList.add('show');
    setTimeout(() => {
      walkthrough.classList.add('reveal');
    }, 200);
    updateScreen();
  };

  return (
    <>
        <div className="container">
        <button className="open-walkthrough" onClick={openModal}>
          Start
        </button>
        <div className="walkthrough show reveal">
          <div className="walkthrough-pagination">
            <a className="dot active"></a>
            <a className="dot"></a>
            <a className="dot"></a>
            <a className="dot"></a>
            <a className="dot"></a>
          </div>
          <div className="walkthrough-body">
            <ul className="screens animate">
              {/* Your screen components go here */}
            </ul>
            <button className="prev-screen" onClick={prevScreen}>
              <i className="icon-angle-left"></i>
            </button>
            <button className="next-screen" onClick={nextScreen}>
              <i className="icon-angle-right"></i>
            </button>
          </div>
          <div className="walkthrough-footer">
            <button className="button next-screen">Next</button>
            <button className="button finish close" disabled="true">
              Finish
            </button>
          </div>
        </div>
      </div>
    </>
  );
};

export default Marketing;
