import React, { Component } from 'react';
import './HeroBanner.css';

class HeroBanner extends Component {
  constructor(props) {
    super(props);
    this.contactFormRef = React.createRef();
  }

  handleScrollToContactForm = () => {
    this.contactFormRef.current.scrollIntoView({ behavior: 'smooth' });
  };

  render() {
    return (
      <div className="neumorphic-hero-banner">
        <div className="content">
          <h1 className="title">Welcome to Our Website</h1>
          <p className="subtitle">Explore the possibilities!</p>
          <button className="scroll-button" onClick={this.handleScrollToContactForm}>
            Contact Us
          </button>
        </div>
      </div>
    );
  }
}

export default HeroBanner;
