import React, { useState } from 'react';
import './Marketing.css';

const Marketing = () => {
  const [index, setIndex] = useState(0);

  const nextScreen = () => {
    if (index < indexMax()) {
      setIndex(index + 1);
    }
  };

  const prevScreen = () => {
    if (index > 0) {
      setIndex(index - 1);
    }
  };

  const updateScreen = () => {
    reset();
    goTo(index);
    setBtns();
  };

  const setBtns = () => {
    const $nextBtn = document.querySelector('.next-screen');
    const $prevBtn = document.querySelector('.prev-screen');
    const $lastBtn = document.querySelector('.finish');

    if (index === indexMax()) {
      $nextBtn.disabled = true;
      $prevBtn.disabled = false;
      $lastBtn.classList.add('active');
      $lastBtn.disabled = false;
    } else if (index === 0) {
      $nextBtn.disabled = false;
      $prevBtn.disabled = true;
      $lastBtn.classList.remove('active');
      $lastBtn.disabled = true;
    } else {
      $nextBtn.disabled = false;
      $prevBtn.disabled = false;
      $lastBtn.classList.remove('active');
      $lastBtn.disabled = true;
    }
  };

  const goTo = (index) => {
    const screens = document.querySelectorAll('.screen');
    const dots = document.querySelectorAll('.dot');

    screens.forEach((screen, idx) => {
      if (idx === index) {
        screen.classList.add('active');
        dots[idx].classList.add('active');
      }
    });
  };

  const reset = () => {
    const screens = document.querySelectorAll('.screen');
    const dots = document.querySelectorAll('.dot');

    screens.forEach((screen) => {
      screen.classList.remove('active');
    });

    dots.forEach((dot) => {
      dot.classList.remove('active');
    });
  };

  const indexMax = () => {
    const screens = document.querySelectorAll('.screen');
    return screens.length - 1;
  };

  const closeModal = () => {
    const walkthrough = document.querySelector('.walkthrough');
    const shade = document.querySelector('.shade');
    walkthrough.classList.remove('reveal');
    setTimeout(() => {
      walkthrough.classList.remove('show');
      setIndex(0);
      updateScreen();
    }, 200);
  };

  const openModal = () => {
    const walkthrough = document.querySelector('.walkthrough');
    const shade = document.querySelector('.shade');
    walkthrough.classList.add('show');
    setTimeout(() => {
      walkthrough.classList.add('reveal');
    }, 200);
    updateScreen();
  };

  return (
    <>
      {/* Replace your HTML content here */}
      {/* ... */}

      {/* Optionally use React event handlers */}
      <button className="next-screen" onClick={nextScreen}>Next</button>
      <button className="prev-screen" onClick={prevScreen}>Previous</button>
      <button className="finish close" onClick={closeModal} disabled>Finish</button>
      <button className="open-walkthrough" onClick={openModal}>Start</button>
    </>
  );
};

export default Marketing;
